import React, { useState } from "react";

const CreatePostModal = ({ onClose }) => {
  const [caption, setCaption] = useState("");
  const [media, setMedia] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMedia(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handlePost = () => {
    console.log("Caption:", caption);
    console.log("Media:", media);
    alert("Post created!");

    // Reset and close modal
    setCaption("");
    setMedia(null);
    setPreviewUrl(null);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center">
      <div className="bg-white w-full max-w-lg rounded-2xl p-6 relative">
        <button
          className="absolute top-3 right-4 text-2xl font-bold text-gray-500 hover:text-gray-800"
          onClick={onClose}
        >
          &times;
        </button>
        <h3 className="text-xl font-semibold mb-4">Create a New Post</h3>

        <textarea
          className="w-full border border-gray-300 p-2 rounded mb-3 focus:outline-none resize-none"
          placeholder="What's on your mind?"
          rows="3"
          value={caption}
          onChange={(e) => setCaption(e.target.value)}
        />

        {previewUrl && (
          <div className="mb-3">
            {media?.type?.startsWith("video") ? (
              <video controls className="w-full rounded">
                <source src={previewUrl} />
              </video>
            ) : (
              <img src={previewUrl} alt="Preview" className="w-full rounded" />
            )}
          </div>
        )}

        <div className="flex items-center justify-between">
          <label className="cursor-pointer text-blue-600 font-medium hover:underline">
            Upload Image/Video
            <input
              type="file"
              accept="image/*,video/*"
              hidden
              onChange={handleMediaChange}
            />
          </label>

          <button
            onClick={handlePost}
            className="bg-blue-600 text-white px-4 py-2 rounded-full hover:bg-blue-700"
          >
            Post
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreatePostModal;
