import React, { useEffect, useState } from "react";
import {
    FaSearch,
    FaBell,
    FaPlus,
    FaTimes,
    FaImage,
    FaVideo,
} from "react-icons/fa";
import userLogo from "../assets/userlogo.png";
import postImage from "../assets/post.jpg";

const MainPage = () => {


    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [showSearchResults, setShowSearchResults] = useState(false);


    const [showFullImage, setShowFullImage] = useState(false);
    const [showComments, setShowComments] = useState(false);
    const [commentText, setCommentText] = useState("");
    const [showCreatePost, setShowCreatePost] = useState(false);
    const [newPostText, setNewPostText] = useState("");
    const [newPostMedia, setNewPostMedia] = useState(null);
    const [notificationsOpen, setNotificationsOpen] = useState(false);

    const [likes, setLikes] = useState(24); // initial like count
    const [liked, setLiked] = useState(false);

    const [userReaction, setUserReaction] = useState(null);

    const [isFollowing, setIsFollowing] = useState(false);


    useEffect(() => {
        const fetchUsers = async () => {
            if (!searchTerm.trim()) {
                setSearchResults([]);
                return;
            }
            try {
                const res = await fetch(`http://localhost:8080/api/users/search?query=${searchTerm}`);
                const data = await res.json();
                setSearchResults(data);
            } catch (err) {
                console.error("Error fetching users:", err);
            }
        };

        const delayDebounce = setTimeout(fetchUsers, 300);
        return () => clearTimeout(delayDebounce);
    }, [searchTerm]);

    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = localStorage.getItem("user");
        if (storedUser) {
            const parsedUser = JSON.parse(storedUser);
            setUser(parsedUser);
            console.log("User Email:", parsedUser); // This will work
        } else {
            setUser(null);
        }
    }, []);
    
    



    // const user = {
    //     name: "John Doe",
    //     profilePic: userLogo,
    //     followers: 120,
    //     following: 80,
    //     active: true,
    // };


    const [reactions, setReactions] = useState({
        love: 4,
        funny: 2,
        wow: 1,
        sad: 0,
        angry: 0,
    });

    const toggleLike = () => {
        setLiked(!liked);
        setLikes((prev) => (liked ? prev - 1 : prev + 1));
    };
    const toggleFollow = () => {
        setIsFollowing((prev) => !prev);
    };


    const emojiMap = {
        love: "❤️",
        funny: "😂",
        wow: "😮",
        sad: "😢",
        angry: "😡",
    };

    const handleReaction = (type) => {
        if (userReaction === type) {
            // Remove existing reaction
            setReactions((prev) => ({
                ...prev,
                [type]: prev[type] - 1,
            }));
            setUserReaction(null);
        } else {
            // Remove previous reaction (if any), then add new one
            setReactions((prev) => {
                const updated = { ...prev };
                if (userReaction) {
                    updated[userReaction] = Math.max(updated[userReaction] - 1, 0);
                }
                updated[type] += 1;
                return updated;
            });
            setUserReaction(type);
        }
    };

    const handleCommentSubmit = () => {
        console.log("Comment Submitted:", commentText);
        setCommentText("");
        setShowComments(false);
    };

    // const handlePostSubmit = () => {
    //     console.log("New Post:", newPostText, newPostMedia);
    //     setNewPostText("");
    //     setNewPostMedia(null);
    //     setShowCreatePost(false);
    // };
    const handlePostSubmit = async () => {
        if (!caption.trim() && mediaFiles.length === 0) {
          alert("Please enter a caption or select media");
          return;
        }
      
        const formData = new FormData();
        formData.append("caption", caption);
        formData.append("userId", user.id);
      
        mediaFiles.forEach(file => {
          formData.append("mediaFiles", file);
        });
      
        try {
          const res = await axios.post("http://localhost:8080/api/posts/upload", formData, {
            headers: { "Content-Type": "multipart/form-data" }
          });
      
          if (res.status === 200) {
            alert("Post uploaded!");
            setCaption("");
            setMediaFiles([]);
            setShowCreatePost(false);
          }
        } catch (err) {
          console.error(err);
          alert("Upload failed.");
        }
      };
      

    //const handleMediaChange = (e) => {
        //setNewPostMedia(URL.createObjectURL(e.target.files[0]));
    //};
    const handleMediaChange = (e) => {
        const files = Array.from(e.target.files);
        if (files.length > 3) {
          alert("You can only upload up to 3 media files.");
          return;
        }
        setMediaFiles(files); // mediaFiles is an array of File
      };
      

    const handleLogout = () => {
        localStorage.removeItem("user");
        setUser(null);
        window.location.href = "/"; // 👈 redirect after logout
    };
    
    return (
        <div className="min-h-screen bg-gray-100 font-sans relative">
            {/* Header */}
            <div className="flex items-center justify-between px-6 py-4 bg-white shadow-md sticky top-0 z-20">
                <h1 className="text-2xl font-bold text-blue-600">SkillShare</h1>
                <div className="flex items-center gap-4 relative">
                    {/* Search */}
                    <div className="relative">
                        <input
                            type="text"
                            placeholder="Search users..."
                            className="pl-10 pr-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 w-full"
                            value={searchTerm}
                            onChange={(e) => {
                                setSearchTerm(e.target.value);
                                setShowSearchResults(true);
                            }}
                            onFocus={() => setShowSearchResults(true)}
                            onBlur={() => setTimeout(() => setShowSearchResults(false), 200)}
                        />
                        <FaSearch className="absolute left-3 top-3 text-gray-500" />

                        {/* Search Result Dropdown */}
                        {showSearchResults && searchResults.length > 0 && (
                            <div className="absolute top-12 left-0 w-full bg-white shadow-lg rounded-xl z-30 max-h-60 overflow-y-auto">
                                {searchResults.map((u) => (
                                    <div
                                        key={u.id}
                                        className="flex items-center gap-3 px-4 py-2 hover:bg-gray-100 cursor-pointer"
                                    >
                                        <img src={u.profilePic} alt="user" className="w-8 h-8 rounded-full" />
                                        <span className="text-sm font-medium">{u.name}</span>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Notification */}
                    <div className="relative">
                        <FaBell
                            className="text-xl text-gray-600 cursor-pointer"
                            onClick={() => setNotificationsOpen(!notificationsOpen)}
                        />
                        {notificationsOpen && (
                            <div className="absolute right-0 mt-2 w-72 bg-white shadow-lg rounded-xl z-30">
                                <div className="p-4 border-b font-semibold text-gray-700">
                                    Notifications
                                </div>
                                <ul className="max-h-60 overflow-y-auto">
                                    <li className="px-4 py-2 hover:bg-gray-100">
                                        💬 Jane commented on your post.
                                    </li>
                                    <li className="px-4 py-2 hover:bg-gray-100">
                                        ❤️ Alex liked your post.
                                    </li>
                                    <li className="px-4 py-2 hover:bg-gray-100">
                                        📌 You have a new follower: Maria.
                                    </li>
                                </ul>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Main Content */}
            <div className="flex flex-col md:flex-row p-6 gap-6">
                {/* User Info */}
                {user && (
                    <div className="bg-white p-4 rounded-2xl shadow-md w-full md:w-1/4 text-center">
                        <img
                            src={`http://localhost:8080/uploads/${user.photo}`}
                            alt="profile"
                            className="w-24 h-24 mx-auto rounded-full border-4 border-blue-500"
                        />
                        <h2 className="mt-4 text-xl font-bold">{user.fullName}</h2>
                        <p className="text-sm text-gray-500">{user.email}</p>

                        {/* Active Now status */}
                        <p className="text-green-500 mt-2 font-medium">🟢 Active Now</p>

                        {/* Optional followers/following placeholders */}
                        <div className="mt-4 flex justify-around">
                            <div>
                                <p className="font-bold">--</p>
                                <p className="text-sm text-gray-500">Followers</p>
                            </div>
                            <div>
                                <p className="font-bold">--</p>
                                <p className="text-sm text-gray-500">Following</p>
                            </div>
                        </div>

                        {/* Logout Button */}
                        <button
                            onClick={handleLogout}
                            className="mt-6 px-4 py-2 bg-red-500 text-white rounded-xl hover:bg-red-600 transition duration-200"
                        >
                            Logout
                        </button>
                    </div>
                )}


                {/* Feed */}
                <div className="w-full md:w-3/4 space-y-6 max-h-[calc(100vh-150px)] overflow-y-auto pr-2">
                    {/* Sample Post */}
                    <div className="bg-white p-4 rounded-2xl shadow-md">
                        <div className="flex items-center gap-4 mb-4">
                            <img
                                src={userLogo}
                                alt="user"
                                className="w-10 h-10 rounded-full"
                            />
                            <div>
                                <div className="flex items-center gap-2">
                                    <h3 className="font-semibold">Jane Smith</h3>
                                    <button
                                        onClick={toggleFollow}
                                        className={`text-sm px-2 py-1 rounded-full transition ${isFollowing
                                            ? "bg-green-100 text-green-600 hover:bg-green-200"
                                            : "bg-blue-100 text-blue-600 hover:bg-blue-200"
                                            }`}
                                    >
                                        {isFollowing ? "Following" : "Follow"}
                                    </button>
                                </div>
                                <p className="text-xs text-gray-400">2 hours ago</p>
                            </div>
                        </div>
                        <p className="mb-3">Excited to share my latest tutorial! 📚</p>
                        <img
                            src={postImage}
                            alt="post"
                            onClick={() => setShowFullImage(true)}
                            className="rounded-lg w-full max-h-[400px] object-cover mb-4 cursor-pointer hover:opacity-90"
                        />
                        {/* Emoji Reactions */}
                        <div className="flex items-center gap-3 text-2xl text-gray-700 mb-2">
                            {Object.keys(emojiMap).map((type) => (
                                <span
                                    key={type}
                                    className={`cursor-pointer ${userReaction === type ? "scale-125 text-blue-600" : "hover:scale-110"
                                        } transition`}
                                    onClick={() => handleReaction(type)}
                                    title={`${reactions[type]} reacted with ${emojiMap[type]}`}
                                >
                                    {emojiMap[type]} <span className="text-sm">{reactions[type]}</span>
                                </span>
                            ))}
                            <span
                                className="text-lg cursor-pointer ml-auto text-gray-500"
                                onClick={() => setShowComments(true)}
                            >
                                💬 12 Comments
                            </span>
                        </div>

                    </div>
                </div>
            </div>

            {/* Floating Post Button */}
            <button
                onClick={() => setShowCreatePost(true)}
                className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full shadow-lg hover:bg-blue-700 transition z-30"
            >
                <FaPlus />
            </button>

            {/* Full Image Modal */}
            {showFullImage && (
                <div className="fixed inset-0 bg-black bg-opacity-80 z-50 flex items-center justify-center">
                    <img src={postImage} alt="full" className="max-h-[90vh] rounded-lg" />
                    <button
                        className="absolute top-6 right-6 text-white text-2xl"
                        onClick={() => setShowFullImage(false)}
                    >
                        <FaTimes />
                    </button>
                </div>
            )}

            {/* Comment Modal */}
            {showComments && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center px-4">
                    <div className="bg-white rounded-2xl w-full max-w-md p-6 relative">
                        <h2 className="text-lg font-semibold mb-4">Comments</h2>
                        <div className="max-h-60 overflow-y-auto space-y-3 mb-4">
                            <p><strong>@user1:</strong> Great tutorial!</p>
                            <p><strong>@user2:</strong> Thanks for sharing 🙌</p>
                        </div>
                        <input
                            type="text"
                            value={commentText}
                            onChange={(e) => setCommentText(e.target.value)}
                            placeholder="Type a comment..."
                            className="w-full px-4 py-2 border rounded-lg mb-3"
                        />
                        <div className="flex justify-end gap-2">
                            <button
                                onClick={() => setShowComments(false)}
                                className="text-gray-600 hover:underline"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleCommentSubmit}
                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                            >
                                Send
                            </button>
                        </div>
                        <button
                            className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
                            onClick={() => setShowComments(false)}
                        >
                            <FaTimes />
                        </button>
                    </div>
                </div>
            )}

            {/* Create Post Modal */}
            {showCreatePost && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center px-4">
                    <div className="bg-white rounded-2xl w-full max-w-md p-6 relative">
                        <h2 className="text-lg font-semibold mb-4">Create New Post</h2>
                        <textarea
                            value={newPostText}
                            onChange={(e) => setNewPostText(e.target.value)}
                            placeholder="What's on your mind?"
                            rows={4}
                            className="w-full px-4 py-2 border rounded-lg mb-3"
                        />
                        <div className="flex items-center justify-between mb-3">
                            <label className="flex items-center gap-2 cursor-pointer">
                                <FaImage className="text-blue-500" />
                                <span className="text-sm">Photo/Video</span>
                                <input
                                    type="file"
                                    accept="image/*,video/*"
                                    onChange={handleMediaChange}
                                    className="hidden"
                                />
                            </label>
                        </div>
                        {newPostMedia && (
                            <img
                                src={newPostMedia}
                                alt="preview"
                                className="w-full rounded-lg mb-3 max-h-60 object-cover"
                            />
                        )}
                        <div className="flex justify-end gap-2">
                            <button
                                onClick={() => setShowCreatePost(false)}
                                className="text-gray-600 hover:underline"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handlePostSubmit}
                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                            >
                                Post
                            </button>
                        </div>
                        <button
                            className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
                            onClick={() => setShowCreatePost(false)}
                        >
                            <FaTimes />
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
