import React, { useState } from "react";
import { FaUserFriends, FaHeart, FaCommentDots, FaLaughSquint, FaSadTear, FaImage, FaTimes } from "react-icons/fa";
import { IoMdClose } from "react-icons/io";
import userLogo from "../assets/userlogo.png";
import samplePost from "../assets/post.jpg";
import defaultAvatar from "../assets/userlogo.png";
import defaultBanner from "../assets/user-banner.jpg";
import { FaPlus } from "react-icons/fa";

const UserProfilePage = () => {
    const [activeTab, setActiveTab] = useState("posts");
    const [showFullImage, setShowFullImage] = useState(false);
    const [fullImageSrc, setFullImageSrc] = useState(null);
    const [showComments, setShowComments] = useState(false);
    const [selectedPost, setSelectedPost] = useState(null);

    const [likes, setLikes] = useState(24); // initial like count
    const [liked, setLiked] = useState(false);

    const [userReaction, setUserReaction] = useState(null);

    const [banner, setBanner] = useState(defaultBanner);
    const [profilePic, setProfilePic] = useState(defaultAvatar);
    const [editing, setEditing] = useState(false);
    const [name, setName] = useState("John Doe");
    const [bio, setBio] = useState("Passionate about teaching and tech!");

    const [showCreatePost, setShowCreatePost] = useState(false);
    const [newPostText, setNewPostText] = useState("");
    const [newPostMedia, setNewPostMedia] = useState(null);

    const [editingPost, setEditingPost] = useState(null);
    const [editPostText, setEditPostText] = useState("");
    const [editPostMedia, setEditPostMedia] = useState(null);


    const user = {
        name: "John Doe",
        bio: "Full-stack developer & tech enthusiast 👨‍💻",
        followers: 1220,
        following: 320,
        isFollowing: false,
        active: true,
    };

    const [reactions, setReactions] = useState({
        love: 4,
        funny: 2,
        wow: 1,
        sad: 0,
        angry: 0,
    });

    const toggleLike = () => {
        setLiked(!liked);
        setLikes((prev) => (liked ? prev - 1 : prev + 1));
    };

    const emojiMap = {
        love: "❤️",
        funny: "😂",
        wow: "😮",
        sad: "😢",
        angry: "😡",
    };

    const handleReaction = (type) => {
        if (userReaction === type) {
            // Remove existing reaction
            setReactions((prev) => ({
                ...prev,
                [type]: prev[type] - 1,
            }));
            setUserReaction(null);
        } else {
            // Remove previous reaction (if any), then add new one
            setReactions((prev) => {
                const updated = { ...prev };
                if (userReaction) {
                    updated[userReaction] = Math.max(updated[userReaction] - 1, 0);
                }
                updated[type] += 1;
                return updated;
            });
            setUserReaction(type);
        }
    };


    const [posts, setPosts] = useState([
        {
            id: 1,
            content: "Just uploaded my latest React Native tutorial! 🔥",
            image: samplePost,
            time: "3h ago",
            reactions: { like: 23, laugh: 4, sad: 2 },
            comments: 8,
        },
    ]);


    const openFullImage = (img) => {
        setFullImageSrc(img);
        setShowFullImage(true);
    };

    const openComments = (post) => {
        setSelectedPost(post);
        setShowComments(true);
    };

    const handleBannerChange = (e) => {
        const file = e.target.files[0];
        if (file) setBanner(URL.createObjectURL(file));
    };

    const handleProfilePicChange = (e) => {
        const file = e.target.files[0];
        if (file) setProfilePic(URL.createObjectURL(file));
    };

    const handlePostSubmit = () => {
        console.log("New Post:", newPostText, newPostMedia);
        setNewPostText("");
        setNewPostMedia(null);
        setShowCreatePost(false);
    };

    const handleMediaChange = (e) => {
        setNewPostMedia(URL.createObjectURL(e.target.files[0]));
    };


    return (
        <div className="min-h-screen bg-gray-100 font-sans">
            {/* Banner */}
            <div className="relative w-full h-64 bg-gray-300">
                <img
                    src={banner}
                    alt="Banner"
                    className="w-full h-full object-cover rounded-b-xl"
                />
                {editing && (
                    <label className="absolute top-2 right-2 bg-white text-sm px-3 py-1 rounded-full cursor-pointer shadow">
                        Change Banner
                        <input type="file" hidden onChange={handleBannerChange} />
                    </label>
                )}


                {/* Profile Picture */}
                <div className="absolute -bottom-16 left-6 w-32 h-32 rounded-full border-4 border-white overflow-hidden shadow-md">
                    <img src={profilePic} alt="Profile" className="w-full h-full object-cover" />
                    {editing && (
                        <label className="absolute bottom-0 bg-black bg-opacity-60 text-white w-full text-center text-xs py-1 cursor-pointer">
                            Edit
                            <input type="file" hidden onChange={handleProfilePicChange} />
                        </label>
                    )}
                </div>
            </div>

            {/* Floating Add Post Button */}
            <button
                onClick={() => setShowCreatePost(true)}
                className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full shadow-lg hover:bg-blue-700 z-40"
                title="Create Post"
            >
                <FaPlus onClick={() => setShowCreatePost(true)} />
            </button>

            {/* Create Post Modal */}
            {showCreatePost && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center px-4">
                    <div className="bg-white rounded-2xl w-full max-w-md p-6 relative">
                        <h2 className="text-lg font-semibold mb-4">Create New Post</h2>
                        <textarea
                            value={newPostText}
                            onChange={(e) => setNewPostText(e.target.value)}
                            placeholder="What's on your mind?"
                            rows={4}
                            className="w-full px-4 py-2 border rounded-lg mb-3"
                        />
                        <div className="flex items-center justify-between mb-3">
                            <label className="flex items-center gap-2 cursor-pointer">
                                <FaImage className="text-blue-500" />
                                <span className="text-sm">Photo/Video</span>
                                <input
                                    type="file"
                                    accept="image/*,video/*"
                                    onChange={handleMediaChange}
                                    className="hidden"
                                />
                            </label>
                        </div>
                        {newPostMedia && (
                            <img
                                src={newPostMedia}
                                alt="preview"
                                className="w-full rounded-lg mb-3 max-h-60 object-cover"
                            />
                        )}
                        <div className="flex justify-end gap-2">
                            <button
                                onClick={() => setShowCreatePost(false)}
                                className="text-gray-600 hover:underline"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handlePostSubmit}
                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                            >
                                Post
                            </button>
                        </div>
                        <button
                            className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
                            onClick={() => setShowCreatePost(false)}
                        >
                            <FaTimes />
                        </button>
                    </div>
                </div>
            )}

            {editingPost && (
                <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center px-4">
                    <div className="bg-white rounded-2xl w-full max-w-md p-6 relative">
                        <h2 className="text-lg font-semibold mb-4">Edit Post</h2>
                        <textarea
                            value={editPostText}
                            onChange={(e) => setEditPostText(e.target.value)}
                            rows={4}
                            className="w-full px-4 py-2 border rounded-lg mb-3"
                        />
                        <div className="flex items-center justify-between mb-3">
                            <label className="flex items-center gap-2 cursor-pointer">
                                <FaImage className="text-blue-500" />
                                <span className="text-sm">Change Media</span>
                                <input
                                    type="file"
                                    accept="image/*,video/*"
                                    onChange={(e) => {
                                        const file = e.target.files[0];
                                        if (file) setEditPostMedia(URL.createObjectURL(file));
                                    }}
                                    className="hidden"
                                />
                            </label>
                        </div>
                        {editPostMedia && (
                            <img
                                src={editPostMedia}
                                alt="preview"
                                className="w-full rounded-lg mb-3 max-h-60 object-cover"
                            />
                        )}
                        <div className="flex justify-end gap-2">
                            <button
                                onClick={() => setEditingPost(null)}
                                className="text-gray-600 hover:underline"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={() => {
                                    // Update the post
                                    const updatedPosts = posts.map(p =>
                                        p.id === editingPost.id
                                            ? { ...p, content: editPostText, image: editPostMedia }
                                            : p
                                    );
                                    setPosts(updatedPosts);
                                    setEditingPost(null);
                                }}
                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                            >
                                Save Changes
                            </button>
                        </div>
                        <button
                            className="absolute top-3 right-3 text-gray-400 hover:text-gray-600"
                            onClick={() => setEditingPost(null)}
                        >
                            <FaTimes />
                        </button>
                    </div>
                </div>
            )}


            {/* User Info */}
            <div className="mt-20 px-6">
                {editing ? (
                    <>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="text-2xl font-bold border-b border-gray-300 focus:outline-none bg-transparent w-full"
                        />

                        <textarea
                            value={bio}
                            onChange={(e) => setBio(e.target.value)}
                            className="mt-2 text-gray-600 border-b border-gray-300 focus:outline-none bg-transparent w-full"
                        />
                    </>
                ) : (
                    <>
                        <h2 className="text-2xl font-bold">{name}</h2>
                        <p className="text-sm text-gray-500">
                            {user.active ? "🟢 Active Now" : "🔴 Offline"}
                        </p>
                        <p className="text-gray-600 mt-1">{bio}</p>
                    </>
                )}
                <button
                    onClick={() => setEditing(!editing)}
                    className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700"
                >
                    {editing ? "Save" : "Edit Profile"}
                </button>
            </div>



            {/* Tabs */}
            <div className="flex justify-center mt-4 gap-6 text-gray-600 font-semibold">
                <button onClick={() => setActiveTab("posts")} className={activeTab === "posts" ? "text-blue-600" : ""}>Posts</button>
                <button onClick={() => setActiveTab("about")} className={activeTab === "about" ? "text-blue-600" : ""}>About</button>
                <button onClick={() => setActiveTab("followers")} className={activeTab === "followers" ? "text-blue-600" : ""}>Followers</button>
            </div>

            {/* Posts */}
            {activeTab === "posts" && (
                <div className="p-6 space-y-6">
                    {posts.map(post => (
                        <div key={post.id} className="bg-white rounded-2xl p-4 shadow-md relative">
                            {/* Edit Icon in Top Right */}
                            <button
                                onClick={() => {
                                    setEditingPost(post); // fix: use current post, not full posts array
                                    setEditPostText(post.content);
                                    setEditPostMedia(post.image);
                                }}
                                className="absolute top-3 right-3 text-gray-400 hover:text-blue-600"
                                title="Edit Post"
                            >
                                ✏️Edit
                            </button>

                            <div className="flex items-center gap-3 mb-2">
                                <img src={userLogo} className="w-10 h-10 rounded-full" alt="user" />
                                <div>
                                    <h4 className="font-bold">{user.name}</h4>
                                    <p className="text-xs text-gray-400">{post.time}</p>
                                </div>
                            </div>

                            <p className="mb-3">{post.content}</p>

                            {post.image && (
                                <img
                                    src={post.image}
                                    alt="post"
                                    onClick={() => openFullImage(post.image)}
                                    className="w-full max-h-[400px] object-cover rounded-xl mb-3 cursor-pointer hover:opacity-90"
                                />
                            )}

                            <div className="flex items-center gap-3 text-2xl text-gray-700 mb-2">
                                {Object.keys(emojiMap).map((type) => (
                                    <span
                                        key={type}
                                        className={`cursor-pointer ${userReaction === type ? "scale-125 text-blue-600" : "hover:scale-110"
                                            } transition`}
                                        onClick={() => handleReaction(type)}
                                        title={`${reactions[type]} reacted with ${emojiMap[type]}`}
                                    >
                                        {emojiMap[type]} <span className="text-sm">{reactions[type]}</span>
                                    </span>
                                ))}
                                <span
                                    className="text-lg cursor-pointer ml-auto text-gray-500"
                                    onClick={() => openComments(true)}
                                >
                                    💬 12 Comments
                                </span>
                            </div>
                        </div>
                    ))}



                </div>
            )}

            {/* About */}
            {activeTab === "about" && (
                <div className="bg-white p-6 rounded-xl shadow-md text-gray-700 m-4">
                    <h3 className="text-lg font-bold mb-2">About</h3>
                    <p>👨‍💻 Full-stack developer at TechCompany</p>
                    <p>📍 Location: New York, USA</p>
                    <p>📧 john.doe@example.com</p>
                </div>
            )}

            {/* Followers */}
            {activeTab === "followers" && (
                <div className="bg-white p-6 rounded-xl shadow-md text-gray-700 m-4">
                    <h3 className="text-lg font-bold mb-4">Followers</h3>
                    <ul className="space-y-3">
                        <li className="flex items-center gap-3">
                            <img src={userLogo} className="w-8 h-8 rounded-full" alt="follower" />
                            <span className="font-medium">Alice Smith</span>
                        </li>
                    </ul>
                </div>
            )}

            {/* Full Image Modal */}
            {showFullImage && (
                <div className="fixed inset-0 bg-black bg-opacity-80 z-50 flex items-center justify-center">
                    <div className="relative">
                        <img src={fullImageSrc} alt="Full" className="max-h-[90vh] rounded-xl" />
                        <button
                            className="absolute top-2 right-2 text-white text-2xl"
                            onClick={() => setShowFullImage(false)}
                        >
                            <IoMdClose />
                        </button>
                    </div>
                </div>
            )}

            {/* Comment Modal */}
            {showComments && selectedPost && (
                <div className="fixed inset-0 bg-black bg-opacity-60 z-50 flex items-center justify-center">
                    <div className="bg-white w-full max-w-md p-6 rounded-xl shadow-lg relative">
                        <button className="absolute top-2 right-2 text-gray-600" onClick={() => setShowComments(false)}>
                            <IoMdClose />
                        </button>
                        <h2 className="text-lg font-bold mb-2">Comments</h2>
                        <div className="space-y-2 max-h-48 overflow-y-auto">
                            <p><strong>Alice:</strong> Great post!</p>
                            <p><strong>Bob:</strong> Really helpful 🔥</p>
                            {/* Mock comments */}
                        </div>
                        <textarea
                            placeholder="Add a comment..."
                            className="w-full mt-4 p-2 border border-gray-300 rounded-xl resize-none"
                            rows={3}
                        ></textarea>
                        <button className="mt-3 bg-blue-600 text-white px-4 py-2 rounded-xl w-full hover:bg-blue-700">
                            Post Comment
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default UserProfilePage;
