import React, { useState } from "react";
import { FaUserFriends, FaHeart, FaCommentDots } from "react-icons/fa";
import userBanner from "../assets/user-banner.jpg";
import userLogo from "../assets/userlogo.png";
import samplePost from "../assets/post.jpg";

const UserProfilePage = () => {
  const [activeTab, setActiveTab] = useState("posts");

  const user = {
    name: "John Doe",
    bio: "Full-stack developer & tech enthusiast üë®‚Äçüíª",
    followers: 1220,
    following: 320,
    isFollowing: false,
  };

  const posts = [
    {
      id: 1,
      content: "Just uploaded my latest React Native tutorial! üî•",
      image: samplePost,
      time: "3h ago",
      likes: 45,
      comments: 8,
    },
    // Add more posts
  ];

  const handleFollowToggle = () => {
    // Logic to follow/unfollow
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      {/* Banner */}
      <div className="relative h-60 bg-cover bg-center" style={{ backgroundImage: `url(${userBanner})` }}>
        <div className="absolute bottom-[-40px] left-6">
          <img
            src={userLogo}
            alt="profile"
            className="w-24 h-24 rounded-full border-4 border-white shadow-md"
          />
        </div>
      </div>

      {/* User Info */}
      <div className="bg-white px-6 pt-16 pb-6 shadow-md rounded-b-2xl">
        <div className="flex justify-between items-center">
          <div>
            <h2 className="text-2xl font-bold">{user.name}</h2>
            <p className="text-gray-600">{user.bio}</p>
            <div className="flex gap-6 mt-2 text-sm text-gray-500">
              <p><FaUserFriends className="inline mr-1" /> {user.followers} Followers</p>
              <p>Following {user.following}</p>
            </div>
          </div>
          <button
            onClick={handleFollowToggle}
            className={`px-4 py-2 rounded-xl font-semibold ${
              user.isFollowing ? "bg-gray-300 text-gray-800" : "bg-blue-600 text-white"
            }`}
          >
            {user.isFollowing ? "Unfollow" : "Follow"}
          </button>
        </div>
      </div>

      {/* Tabs */}
      <div className="flex justify-center mt-4 gap-6 text-gray-600 font-semibold">
        <button onClick={() => setActiveTab("posts")} className={activeTab === "posts" ? "text-blue-600" : ""}>Posts</button>
        <button onClick={() => setActiveTab("about")} className={activeTab === "about" ? "text-blue-600" : ""}>About</button>
        <button onClick={() => setActiveTab("followers")} className={activeTab === "followers" ? "text-blue-600" : ""}>Followers</button>
      </div>

      {/* Tab Content */}
      <div className="p-6">
        {activeTab === "posts" && (
          <div className="grid gap-6">
            {posts.map(post => (
              <div key={post.id} className="bg-white rounded-2xl p-4 shadow-md">
                <div className="flex items-center gap-3 mb-2">
                  <img src={userLogo} className="w-10 h-10 rounded-full" alt="user" />
                  <div>
                    <h4 className="font-bold">{user.name}</h4>
                    <p className="text-xs text-gray-400">{post.time}</p>
                  </div>
                </div>
                <p className="mb-3">{post.content}</p>
                <img src={post.image} alt="post" className="w-full h-64 object-cover rounded-xl mb-3" />
                <div className="flex gap-6 text-gray-600 text-lg">
                  <span><FaHeart className="inline" /> {post.likes}</span>
                  <span><FaCommentDots className="inline" /> {post.comments}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {activeTab === "about" && (
          <div className="bg-white p-6 rounded-xl shadow-md text-gray-700">
            <h3 className="text-lg font-bold mb-2">About</h3>
            <p>üë®‚Äçüíª Full-stack developer at TechCompany</p>
            <p>üìç Location: New York, USA</p>
            <p>üìß john.doe@example.com</p>
          </div>
        )}

        {activeTab === "followers" && (
          <div className="bg-white p-6 rounded-xl shadow-md text-gray-700">
            <h3 className="text-lg font-bold mb-4">Followers</h3>
            <ul className="space-y-3">
              <li className="flex items-center gap-3">
                <img src={userLogo} className="w-8 h-8 rounded-full" alt="follower" />
                <span className="font-medium">Alice Smith</span>
              </li>
              {/* Add more followers */}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfilePage;
